package LU3;

//Worksheet 3 Demonstration code of a While Loop
public class Demo_WS3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num = 8;
		int i = 1;
		while ( i < 11 ) {
		  System.out.println( num + " x " + i + " = " + (num * i) );
		  i = i + 1;
		}

	} // end Main
} // end Class
